import React, { Component } from "react";
import { List } from "antd";
import "./view6.css";
import "bootstrap/dist/css/bootstrap.css";
export default class View6 extends Component {
  selectUser = (user) => {
    this.props.changeSelectUser(user);
  };
  /*

<div className="header">User List</div>
        <List
          size="small"
          bordered
          dataSource={data}
          renderItem={(user) => (
            <List.Item onClick={() => this.selectUser(user)}>
              <div>{user.name + ":" + user.age}</div>
            </List.Item>
          )}
        />
      </div>


*/
  render() {
    const { countryName, selectedCountry } = this.props;
    return (
      <div id="view6" className="pane">
        <div className="row">
          <div className="col-4">
            <div class="badge badge-primary text-wrap">Set X Axis</div>
            <ul className="list-group">
              {countryName.map((country) => (
                <li
                  className={
                    selectedCountry === country
                      ? "list-group-item active"
                      : "list-group-item"
                  }
                  onClick={() => this.props.onSelectX(country)}
                  key={country}
                >
                  {country}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
