import React, { Component } from "react";
class ListGroup extends Component {
  render() {
    const { list, currentGroup, onGroupChange } = this.props;
    return (
      <ul class="list-group">
        {list.map((group) => (
          <li
            className={
              group === currentGroup
                ? "list-group-item active"
                : "list-group-item"
            }
            key={group.id}
            onClick={() => onGroupChange(group)}
          >
            {group.name}
          </li>
        ))}
      </ul>
    );
  }
}
ListGroup.defaultProps = {
  textProperty: "name",
  valueProperty: "_id",
};
export default ListGroup;
