import * as d3 from "d3";

import {
  select,
  geoNaturalEarth1,
  scaleSqrt,
  max,
  format,
  selectAll,
  tsv,
  json,
} from "d3";
import { color } from "../../d3/Color";
import d3Tip from "d3-tip";
import { feature } from "topojson";
import "./style.css";
import "./earth.css";
import "./d3tip.css";
const draw = (props) => {
  d3.select("#mainsvg").remove(); //删除整个SVG
  d3.select("#mainsvg").selectAll("*").remove(); //清空SVG中的内容
  const { data, h, w, attr, year } = props;
  let svg = d3
    .select(".vis-barchart")
    .append("svg")
    .attr("height", h)
    .attr("width", w)
    .attr("class", "svgs")
    .attr("id", "mainsvg");

  const width = +svg.attr("width");
  const height = +svg.attr("height");
  //const margin = { top: 20, right: 20, bottom: 20, left: 20 };
  //const innerWidth = width - margin.left - margin.right;
  //const innerHeight = height - margin.top - margin.bottom;
  const innerWidth = width;
  const innerHeight = height;

  const g = svg.append("g").attr("id", "maingroup");
  //.attr("transform", `translate(${margin.top}, ${margin.right})`);
  const projection = d3.geoNaturalEarth1();
  const pathGenerator = d3.geoPath().projection(projection);

  // setting up the tip tool;
  /*
  const tip = d3
    .tip()
    .attr("class", "d3-tip")
    .html(function (d) {
      return d.properties.name;
    });
  svg.call(tip);
    */

  let lastid = undefined;
  Promise.all([
    tsv("https://unpkg.com/world-atlas@1.1.4/world/50m.tsv"),
    json("https://unpkg.com/world-atlas@1.1.4/world/50m.json"),
  ]).then(([tsvData, topoJSONdata]) => {
    // parses tsvData to extract country names for base map titles
    const countryName = {};
    tsvData.forEach((d) => {
      countryName[d.iso_n3] = d.name;
    });

    // draws a path for each country with countryName as title (shown on hover)
    var worldmeta = feature(topoJSONdata, topoJSONdata.objects.countries);
    projection.fitSize([innerWidth, innerHeight], worldmeta);

    g.selectAll("path")
      .data(worldmeta.features)
      .enter()
      .append("path")
      .attr("class", "country")
      .attr("d", pathGenerator)
      .append("title")
      .text((d) => countryName[d.id]);

    /*
      .attr("stroke", "grey")
      .attr("stroke-width", 1)
      .on("mouseover", function (d) {
        d3.select(this)
          .attr("opacity", 0.5)
          .attr("stroke", "white")
          .attr("stroke-width", 3);
      })
      .on("mouseout", function (d) {
        //不能用箭头函数
        d3.select(this)
          .attr("opacity", 1)
          .attr("stroke", "black")
          .attr("stroke-width", 1);
      });
      */
    //.append("title")
    //.text((d) => countryName[d.id]);
  });

  const radiusValue = (d) => (+d.datum > 0 ? +d.datum : -+d.datum);

  const sizeScale = scaleSqrt()
    .domain([0, max(data, (d) => +d.datum, radiusValue)])
    .range([0, 15]);

  g.selectAll("circle")
    .data(data)
    .enter()
    .append("circle")
    //.attr("class", "country-circle")
    // setting x and y coordiantes by translating country coordinate data to pixels
    //.attr("transform", function (d) {
    //return "translate(" + projection([0, 0]) + ")";
    .attr("transform", function (d) {
      return "translate(" + projection([d.long - 10, d.lat - 30]) + ")";
    })
    //})
    .attr("r", (d) => sizeScale(radiusValue(d)))
    .attr("fill", "red")
    .attr("opacity", 0.8)
    .append("title")
    .text(
      (d) => d.country + ": " + d.datum //+ ": " + format(",")(d[`${caseType}`])
    );
  /*
  svg.call(
    zoom().on("zoom", () => {
      g.attr("transform", event.transform);
    })
  );
*/
  /*
  .on("contextmenu", function (d) {
    //d3.event.preventDefault();
    if (lastid !== d.properties.name) {
      tip.show(d);
      lastid = d.properties.name;
    } else {
      tip.hide(d);
    }
  });
  */
};

export default draw;
