import React, { Component } from "react";
import Like from "./Like";
class MoviesTable extends Component {
  state = {};
  raiseSort = (path) => {
    const sortColumn = { ...this.props.sortColumn };
    if (sortColumn.path === path)
      //already sorted: toggle
      sortColumn.order = sortColumn.order === "asc" ? "desc" : "asc";
    else {
      sortColumn.path = path; //not sorted: sort
      sortColumn.order = "asc";
    }
    console.log(sortColumn.order);
    this.props.onSort(sortColumn);
  };

  render() {
    const { movies, onDelete, onLike, onSort } = this.props;
    return (
      <table className="table">
        <thead>
          <tr>
            <th
              onClick={() => raiseSort("title")}
              style={{ cursor: "pointer" }}
            >
              {" "}
              Title{" "}
            </th>
            <th
              onClick={() => raiseSort("genre.name")}
              style={{ cursor: "pointer" }}
            >
              {" "}
              Genre{" "}
            </th>
            <th
              onClick={() => raiseSort("numberInStock")}
              style={{ cursor: "pointer" }}
            >
              {" "}
              Stock
            </th>
            <th
              onClick={() => raiseSort("dailyRentalRate")}
              style={{ cursor: "pointer" }}
            >
              {" "}
              Rate{" "}
            </th>
            <th> </th>
            <th />
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie._id}>
              <td>{movie.title}</td>
              <td>{movie.genre.name}</td>
              <td>{movie.numberInStock}</td>
              <td>{movie.dailyRentalRate}</td>
              <td>
                <Like liked={movie.liked} onLike={() => onLike(movie)} />
              </td>
              <td>
                <button
                  onClick={() => onDelete(movie)}
                  className="btn btn-danger btn-sm"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
