  const g = svg
    .append("g")
    .attr("transform", `translate(${margin.left}, ${margin.top})`)
    .attr("id", "maingroup");

  const yAxis = d3.axisLeft(yScale).tickSize(-innerWidth).tickPadding(10); // .tickPadding is used to prevend intersection of ticks;
  const xAxis = d3.axisBottom(xScale).tickSize(-innerHeight).tickPadding(10);

  let yAxisGroup = g.append("g").call(yAxis).attr("id", "yaxis");
