import React, { Component } from "react";
class ListGroup extends Component {
  render() {
    const {
      list,
      textProperty,
      valueProperty,
      currentGroup,
      onGroupChange,
    } = this.props;
    return (
      <ul class="list-group">
        <li
          className={
            group === currentGroup
              ? "list-group-item active"
              : "list-group-item"
          }
          key={0}
          onClick={() => onGroupChange(null)}
        >
          {group[textProperty]}
        </li>
        {list.map((group) => (
          <li
            className={
              group === currentGroup
                ? "list-group-item active"
                : "list-group-item"
            }
            key={group[valueProperty]}
            onClick={() => onGroupChange(group)}
          >
            {group[textProperty]}
          </li>
        ))}
      </ul>
    );
  }
}
ListGroup.defaultProps = {
  textProperty: "name",
  valueProperty: "_id",
};
export default ListGroup;
