//installed bootstrap, font-awesome

//command: npm i bootstrap@4.1.1 font-awesome@4.7.0
import React, { Component } from "react";
import { getMovies } from "../fakeMovieService";

import Pagination from "./pagination";
import { paginate } from "../util/paginate";
import { getGenres } from "../fakeGenreService";
import { filt } from "../util/filt";
import ListGroup from "./filter";
import MoviesTable from "./movieTable";
import _ from "lodash";
class Movies extends Component {
  getRealMovies() {
    var movies = getMovies();
    //movies.liked = false;
    console.log(movies);
    return movies;
  }
  /*
  constructor() {
    super();
    this.state = getMovies();
  }
  */
  state = {
    movies: [], //.map((movie) => (movie["liked"] = false)),
    pageSize: 4,
    currentPage: 1,
    list: [],
    currentGroup: null,
    sortColumn: { path: "title", order: "asc" },
  };

  componentDidMount() {
    this.setState({ movies: this.getRealMovies(), list: getGenres() });
  }
  movieList() {
    const m = this.state.movies;
    const items = m.map(
      (movie) => `<tr>
			<td>${movie._id}</td>
			<td>${movie.title}</td>
			<td>${movie.genre.name}</td>
			<td>${movie.numberInStock}</td>
      <td>${movie.dailyRentalRate}</td>
			</tr>`
    );
    return items;
  }
  handleLike = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie }; //creating a new object and assign to it rather than modifying directly
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };
  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies: movies });
  };
  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };
  handleGroupChange = (group) => {
    console.log(this.state.list);
    this.setState({ currentGroup: group, currentPage: 1 });
  };
  handleSort = (path) => {
    this.setState({ sortColumn: { path, order: "asc" } });
  };
  //<table border="1">{this.movieList}</table>
  render() {
    const items = this.movieList();
    const filtedMovies = filt(this.state.movies, this.state.currentGroup);
    _.orderBy(filtered, [this.state.sortColumn.path]);
    const movies = paginate(
      filtedMovies,
      this.state.currentPage,
      this.state.pageSize
    );
    console.log(filtedMovies.length);
    if (this.state.movies.length === 0) return <p> No movies. </p>;
    return (
      <div className="row">
        <div className="col-3">
          <ListGroup
            list={this.state.list}
            currentGroup={this.state.currentGroup}
            onGroupChange={this.handleGroupChange}
          />
        </div>
        <div className="col">
          We have {this.state.movies.length} moives in total.
          <MoviesTable
            movies={movies}
            onDelete={this.handleDelete}
            onLike={this.handleLike}
            onSort={this.handleSort}
          />
          <Pagination
            itemsCount={filtedMovies.length}
            pageSize={this.state.pageSize}
            onPageChange={this.handlePageChange}
            currentPage={this.state.currentPage}
          />
        </div>
      </div>
      //</React.Fragment>
    );
  }
}
export default Movies;
