import * as d3 from "d3";
import {
  select,
  geoNaturalEarth1,
  scaleSqrt,
  max,
  format,
  selectAll,
  tsv,
  json,
} from "d3";
import { color } from "../../npd3/Color";
import d3Tip from "d3-tip";
import { feature } from "topojson";
import "./style.css";
import "./earth.css";
import "./d3tip.css";
const draw = (props) => {
  //d3.select("#svgID").remove(); //删除整个SVG
  //d3.select("#svgID").selectAll("*").remove(); //清空SVG中的内容
  const { data, h, w, attr } = props;
  let svg = d3
    .select(".vis-barchart")
    .append("svg")
    .attr("height", h)
    .attr("width", w)
    .attr("class", "svgs")
    .attr("id", "mainsvg");
  const width = +svg.attr("width");
  const height = +svg.attr("height");
  const margin = { top: 20, right: 20, bottom: 20, left: 20 };
  const innerWidth = width - margin.left - margin.right;
  const innerHeight = height - margin.top - margin.bottom;
  const g = svg
    .append("g")
    .attr("id", "maingroup")
    .attr("transform", `translate(${margin.top}, ${margin.right})`);
  const projection = d3.geoNaturalEarth1();
  //const projection = d3.geoTransverseMercator();
  const pathGenerator = d3.geoPath().projection(projection);

  // setting up the tip tool;
  /*
  const tip = d3
    .tip()
    .attr("class", "d3-tip")
    .html(function (d) {
      return d.properties.name;
    });
  svg.call(tip);
    */
  let worldmeta;
  let lastid = undefined;

  // convert topo-json to geo-json;
  worldmeta = feature(data, data.objects.countries);

  // this code is really important if you want to fit your geoPaths (map) in your SVG element;
  projection.fitSize([innerWidth, innerHeight], worldmeta);

  // perform data-join;
  const paths = g
    .selectAll("path")
    .data(worldmeta.features, (d) => d.properties.name)
    .enter()
    .append("path")
    .attr("d", pathGenerator)
    .attr("stroke", "black")
    .attr("stroke-width", 1)
    .on("mouseover", function (d) {
      d3.select(this)
        .attr("opacity", 0.5)
        .attr("stroke", "white")
        .attr("stroke-width", 3);
    })
    .on("mouseout", function (d) {
      //不能用箭头函数
      d3.select(this)
        .attr("opacity", 1)
        .attr("stroke", "black")
        .attr("stroke-width", 1);
    });
  Promise.all([
    tsv("https://unpkg.com/world-atlas@1.1.4/world/50m.tsv"),
    json("https://unpkg.com/world-atlas@1.1.4/world/50m.json"),
  ]).then(([tsvData, topoJSONdata]) => {
    // parses tsvData to extract country names for base map titles
    const countryName = {};
    tsvData.forEach((d) => {
      countryName[d.iso_n3] = d.name;
    });
    console.log(countryName);
    // draws a path for each country with countryName as title (shown on hover)
    /*
    const countries = feature(topoJSONdata, topoJSONdata.objects.countries);
    g.selectAll("path")
      .data(countries.features)
      .enter()
      .append("path")
      .attr("class", "country")
      .attr("d", pathGenerator)
      .append("title")
      .text((d) => countryName[d.id]);
       */
  });

  const radiusValue = (d) => d[`${attr}`];

  const sizeScale = scaleSqrt()
    .domain([0, max(data, (d) => d[`${attr}`], radiusValue)])
    .range([0, 5]);

  g.selectAll("circle")
    .data(data)
    .enter()
    .append("circle")
    .attr("class", "country-circle")
    // setting x and y coordiantes by translating country coordinate data to pixels
    .attr("transform", function (d) {
      return (
        "translate(" + projection([d.countryInfo.long, d.countryInfo.lat]) + ")"
      );
    })
    .attr("r", (d) => sizeScale(radiusValue(d)))
    .attr("fill", color(attr))
    .append("title");
  //.text(
  // (d) => d.country + " " + caseTitle + ": " + format(",")(d[`${caseType}`])
  //);

  /*
  svg.call(
    zoom().on("zoom", () => {
      g.attr("transform", event.transform);
    })
  );
*/
  /*
  .on("contextmenu", function (d) {
    //d3.event.preventDefault();
    if (lastid !== d.properties.name) {
      tip.show(d);
      lastid = d.properties.name;
    } else {
      tip.hide(d);
    }
  });
  */
};

export default draw;
