import * as d3 from "d3";
import "./style.css";

const draw = (props) => {
  /*
    d3.select('.vis-barchart > *').remove();
    const data = props.data;
    const margin = {top: 20, right: 20, bottom: 30, left: 40};
    const width = props.width - margin.left - margin.right;
    const height = props.height - margin.top - margin.bottom;
    let svg = d3.select('.vis-barchart').append('svg')
            .attr('width',width + margin.left + margin.right)
            .attr('height',height + margin.top + margin.bottom)
            .append("g")
            .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    // format the data
    data.forEach(function(d) {
        d.age = +d.age;
    });

    // Scale the range of the data in the domains
    let x = d3.scaleBand()
          .range([0, width])
          .padding(0.1);
    let y = d3.scaleLinear()
          .range([height, 0]);
    x.domain(data.map(function(d) { return d.name; }));
    y.domain([0, d3.max(data, function(d) { return d.age; })]);

    // append the rectangles for the bar chart
    svg.selectAll(".bar")
        .data(data)
        .enter().append("rect")
        .attr("class", "bar")
        .attr("x", function(d) { return x(d.name); })
        .attr("width", x.bandwidth())
        .attr("y", function(d) { return y(d.age); })
        .attr("height", function(d) { return height - y(d.age); });

    // add the x Axis
    svg.append("g")
        .attr("transform", "translate(0," + height + ")")
        .call(d3.axisBottom(x));

    // add the y Axis
    svg.append("g")
        .call(d3.axisLeft(y));


    */
  d3.select("#svgID").remove(); //删除整个SVG
  d3.select("#svgID").selectAll("*").remove(); //清空SVG中的内容
  const { data, h, w, attr1, attr2 } = props;
  const margin = { top: 100, right: 120, bottom: 100, left: 120 };
  const innerWidth = w - margin.left - margin.right;
  const innerHeight = h - margin.top - margin.bottom;
  const xAxisLabel = attr1;
  const yAxisLabel = attr2;

  let xValue = (d) => d[props.attr1];
  let yValue = (d) => d[props.attr2];

  let xScale = d3
    .scaleLinear()
    .domain([0, d3.max(data, xValue)]) // "extent" is equivalent to ;
    .range([0, innerWidth])
    .nice();
  let yScale = d3
    .scaleLinear()
    .domain([0, d3.max(data, yValue)].reverse()) // remember to use reverse() to make y-axis start from the bottom;
    .range([0, innerHeight])
    .nice();

  const svg = d3
    .select(".vis-barchart")
    .append("svg")
    .attr("width", w)
    .attr("height", h)
    .attr("id", "svgID")
    .style("margin-left", 0);

  const g = svg
    .append("g")
    .attr("transform", `translate(${margin.left}, ${margin.top})`)
    .attr("id", "maingroup");

  const yAxis = d3.axisLeft(yScale).tickSize(-innerWidth).tickPadding(10); // .tickPadding is used to prevend intersection of ticks;
  const xAxis = d3.axisBottom(xScale).tickSize(-innerHeight).tickPadding(10);

  let yAxisGroup = g.append("g").call(yAxis).attr("id", "yaxis");
  yAxisGroup
    .append("text")
    .attr("font-size", "2em")
    .attr("transform", `rotate(-90)`)
    .attr("x", -innerHeight / 2)
    .attr("y", -60)
    .attr("fill", "#333333")
    .text(yAxisLabel)
    .attr("text-anchor", "middle"); // Make label at the middle of axis.
  yAxisGroup.selectAll(".domain").remove(); // we can select multiple tags using comma to seperate them and we can use space to signify nesting;

  let xAxisGroup = g
    .append("g")
    .call(xAxis)
    .attr("transform", `translate(${0}, ${innerHeight})`)
    .attr("id", "xaxis");
  xAxisGroup
    .append("text")
    .attr("font-size", "2em")
    .attr("y", 60)
    .attr("x", innerWidth / 2)
    .attr("fill", "#333333")
    .text(xAxisLabel);
  xAxisGroup.selectAll(".domain").remove();

  let circleupdates = g.selectAll("circle").data(data);
  let circleenter = circleupdates
    .enter()
    .append("circle")
    .attr("cx", (d) => xScale(xValue(d)))
    .attr("cy", (d) => yScale(yValue(d)))
    .attr("r", 5)
    .attr("fill", "green");
};

export default draw;
